// Importez Navigate depuis react-router-dom
import { Navigate } from 'react-router-dom';

// Fonction pour gérer la déconnexion
const handleLogout = () => {
  // Détruisez le token stocké localement
  localStorage.removeItem('token');
  // Redirigez l'utilisateur vers la page de connexion (login)
  return <Navigate to="/login" />;
};

// Dans votre composant Compte, utilisez la fonction handleLogout lorsqu'un utilisateur clique sur le bouton de déconnexion
function Compte() {
  return (
    <div>
      {/* Votre contenu de la page Compte ici */}
      <button onClick={handleLogout}>Déconnexion</button>
    </div>
  );
}

export default Compte;
