import './register.css';
import { useState } from 'react';


const Element = () => (
    <div className='element'>
      <Titre />
      <Formulaire />

    </div>
  )





const Titre =() => (
    <h1 className='principal'>
      Formulaire de contact 
    </h1>
  )

  const Formulaire = () =>{
    const handleEvent = (e) => {
      e.preventDefault(e)
      const f = new FormData(e.target)
      const vrai = []

      f.forEach((value, key) => {
        vrai[key] = value;
      });
      console.log(vrai)
      window.location.href = '/login';

    }

    return ( <div className='englobe'>
    <form onSubmit={handleEvent} className="f">
      <div className="form-example">
        <input type="text" name="name" id="name" placeholder="Rentrer votre nom"  required />
      </div>
      <div className="form-example">
        <input type="email" name="email" id="email" placeholder="Rentrer votre mail" required />
      </div>
      <div className="form-example">
        <input className="sub" type="submit" value="Enregistrer" />
      </div>
    </form>
    </div>)

  }


  function Register() {
    return(
      <Element />
    )
  }

  export default Register;
