import React, { useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        // Authentification réussie, récupérer le token du serveur (adapté à votre API)
        const data = await response.json();
        const token = data.token;
        console.log('Token reçu :', token);

        // Vous pouvez stocker le token dans le state, localStorage, etc.
      } else {
        // Authentification échouée, gérer les erreurs
        const data = await response.json();
        setErrorMessage(data.message || 'Erreur d\'authentification');
      }
    } catch (error) {
      console.error('Erreur lors de l\'authentification :', error);
      setErrorMessage('Erreur d\'authentification');
    }
  };

  return (
    <div className='log'>
      <h1>Connexion</h1>
      <form className="f" onSubmit={handleLogin}>
        <div className="ex">
          <input
            type="email"
            name="email"
            id="email"
            placeholder="Rentrer votre email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="ex">
          <input
            type="password"
            name="password"
            id="password"
            placeholder="Rentrer votre mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="ex">
          <input className="sub" type="submit" value="Connexion" />
        </div>
      </form>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default Login;
